/**
 * Basename
 * Returns the basename of a given file. 
 *
 * Usage:
 *     {{base "docs/toc.md"}}
 *     
 * Returns:
 *     toc
 */
Handlebars.registerHelper('basename', function(base, ext) {
  var fullName = path.basename(base, ext);
  base = path.basename(base, path.extname(fullName))
  return base;
});
