/**
 * keyValue
 * Iterate over an object, setting 'key' and 'value' for each property in the object.
 * 
 * Credit: [@strathmeyer](https://gist.github.com/strathmeyer)
 *
 * Example: 
 *     {{#keyValue obj}} 
 *       Key: {{key}} 
 *       Value: {{value}} 
 *     {{/keyValue}}
 *     
 */
Handlebars.registerHelper("keyValue", function(obj, fn) {
  var buffer = "",
      key;

  for (key in obj) {
    if (obj.hasOwnProperty(key)) {
      buffer += fn({
        key: key,
        value: obj[key]
      });
    }
  }
  return buffer;
});